mapper.update:
UPDATE party_board_master
SET board_title = #{boardTitle},
board_contents = #{boardContents}
WHERE board_seq = #{boardSeq}

mapper.select:
SELECT board_pic_file AS fileName
FROM party_board_pic
WHERE board_pic_board_seq = #{boardSeq}

mapper.delete:
DELETE FROM party_board_master WHERE board_seq = #{boardSeq}
AND board_member_seq = #{boardMemberSeq}
AND board_party_seq = #{boardPartySeq}

mapper.update:
UPDATE party_board_master
SET board_hit = board_hit + 1
WHERE board_seq = #{boardSeq}
