mapper.select:
SELECT COALESCE(B.member_role, 0) AS memberRole
FROM user_master A
LEFT JOIN party_member B ON A.user_seq = B.member_user_seq AND B.member_party_seq = #{partySeq}
LEFT JOIN party_master C ON B.member_party_seq = C.party_seq
WHERE A.user_seq = #{userSeq}

SELECT COUNT(party_seq)
FROM party_master
WHERE party_seq = #{partySeq}

SELECT COUNT(user_seq)
FROM user_master
WHERE user_seq = #{userSeq}

SELECT COUNT(party_name)
FROM party_master
WHERE party_name = #{partyName}
AND party_seq != #{partySeq}

SELECT COUNT(join_seq)
FROM party_join
WHERE join_party_seq = #{partySeq}
AND join_user_seq = #{userSeq}

SELECT COUNT(member_party_seq)
FROM party_member
WHERE member_party_seq = #{partySeq}
AND member_user_seq = #{userSeq}

SELECT member_user_seq
FROM party_member
WHERE member_user_seq = #{memberUserSeq}

SELECT join_party_seq
FROM party_join
WHERE join_party_seq = #{joinPartySeq}

SELECT join_user_seq
FROM party_join
WHERE join_user_seq = #{joinUserSeq}

SELECT party_seq
FROM party_master
WHERE party_seq = #{budgetPartySeq}

SELECT member_seq
FROM party_member
WHERE member_party_seq = #{memberPartySeq}
AND member_seq = #{memberSeq}

SELECT budget_seq
FROM party_budget
WHERE budget_seq = #{budgetSeq}

SELECT plan_seq
FROM plan_master
WHERE plan_seq = #{planSeq}

SELECT party_seq
FROM party_master
WHERE party_seq = #{planPartySeq}

SELECT A.plmember_seq
FROM plan_member A
JOIN party_member B
ON A.plmember_member_seq = B.member_seq
WHERE A.plmember_plan_seq = #{plmemberPlanSeq}
AND B.member_seq = #{memberSeq}

SELECT plan_completed AS planCompleted
FROM plan_master
WHERE plan_seq = #{planSeq}

SELECT 1
FROM review_master
WHERE review_plan_seq = #{reviewPlanSeq}
AND review_plmember_seq = #{reviewPlmemberSeq}

SELECT 1
FROM review_master
WHERE review_seq = #{reviewSeq}
