mapper.select:
SELECT A.review_seq as reviewSeq , A.review_plan_seq as reviewPlanSeq , A.review_plmember_seq as reviewPlmemberSeq , D.user_seq as userSeq , D.user_name as userName , D.user_pic as userPic , A.review_contents as reviewContents , A.review_rating as reviewRating , A.input_dt as inputDt , E.party_seq as partySeq , E.party_name as partyName , IF(F.user_name IS null, "모임장 없음", F.user_name) AS president , IFNULL(G.favCnt, 0) AS favCnt FROM review_master A JOIN plan_member B ON A.review_plmember_seq = B.plmember_seq JOIN party_member C ON B.plmember_member_seq = C.member_seq JOIN user_master D ON C.member_user_seq = D.user_seq JOIN party_master E ON C.member_party_seq = E.party_seq LEFT JOIN (SELECT A.member_seq, B.USER_NAME, A.member_party_seq FROM party_member A JOIN user_master B ON A.member_user_seq = b.user_seq WHERE A.member_role = '1') F ON F.member_party_seq = E.party_seq LEFT JOIN (SELECT COUNT(*) AS favCnt, review_fav_review_seq from review_fav GROUP BY review_fav_review_seq) G ON A.review_seq = G.review_fav_review_seq WHERE D.user_seq = #{userSeq}
AND (TRIM(REPLACE(E.party_name,' ','')) LIKE concat('%',#{searchData},'%') || F.USER_NAME LIKE concat('%',#{searchData},'%') || D.user_name LIKE concat('%',#{searchData},'%'))
AND TRIM(REPLACE(E.party_name,' ','')) LIKE concat('%',#{searchData},'%') AND F.USER_NAME LIKE concat('%',#{searchData},'%') AND D.user_name LIKE concat('%',#{searchData},'%') ORDER BY review_seq DESC
LIMIT #{startIdx}, #{size}

SELECT COUNT(*) FROM review_master A JOIN plan_member B ON A.review_plmember_seq = B.plmember_seq JOIN party_member C ON B.plmember_member_seq = C.member_seq JOIN user_master D ON C.member_user_seq = D.user_seq JOIN party_master E ON C.member_party_seq = E.party_seq LEFT JOIN (SELECT A.member_seq, B.USER_NAME, A.member_party_seq FROM party_member A JOIN user_master B ON A.member_user_seq = b.user_seq WHERE A.member_role = '1') F ON F.member_party_seq = E.party_seq WHERE 1 = 1 AND D.user_seq = #{userSeq}
AND (TRIM(REPLACE(E.party_name,' ','')) LIKE concat('%',#{searchData},'%') || F.USER_NAME LIKE concat('%',#{searchData},'%') || D.user_name LIKE concat('%',#{searchData},'%'))
AND TRIM(REPLACE(E.party_name,' ','')) LIKE concat('%',#{searchData},'%') AND F.USER_NAME LIKE concat('%',#{searchData},'%') AND D.user_name LIKE concat('%',#{searchData},'%')

mapper.update:
UPDATE review_master
SET review_contents = #{reviewContents}
, review_rating = #{reviewRating}
WHERE review_seq = #{reviewSeq}

mapper.delete:
DELETE FROM review_fav
WHERE review_fav_review_seq = #{reviewFavReviewSeq}
AND review_fav_user_seq = #{reviewFavUserSeq}
