SET user_email = #{userEmail}
, user_pw = #{userPw}
, user_name = #{userName}
, user_addr = #{userAddr}
, user_nickname = #{userNickname}
, user_fav = #{userFav}
, user_birth = #{userBirth}
, user_gender = #{userGender}
, user_phone = #{userPhone}
, user_intro = #{userIntro}
, user_pic = #{userPic}
, user_role = #{userRole}

mapper.select:
SELECT user_pw AS userPw
FROM user_master
WHERE user_seq = #{userSeq}

mapper.update:
